mapper.select:
SELECT 
FROM conversation_messages
WHERE user_id = #{userId}
  AND conversation_id = #{conversationId}
ORDER BY created_at ASC

mapper.delete:
DELETE FROM conversation_messages
WHERE user_id = #{userId}
  AND conversation_id = #{conversationId}

